import React, { useEffect, useState } from 'react'
import { FlatList, StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import Task from './Task'
import { StatusBar } from 'expo-status-bar'
import { Modal } from 'react-native-web'

export default function List() {

    const [taskItems, setTaskItems] = useState([])
    const [showProfile, setShowProfile] = useState(false)
    const [task,setTask]=useState()
    useEffect(() => {
        fetchData()
    }, [])

    const fetchData = async () => {
        try {
            const response = await fetch('https://api.unsplash.com/photos/?client_id=ZXjOAAdwefwfYGtyhjJmAerkWnGDxNNnEwTlnHkSqk4')
            const jsonData = await response.json()
            setTaskItems(jsonData)
            console.log(jsonData)
        } catch (error) {
            console.error(error)
        }
    }

    //const getProfile = (task) => {

    //}

    const Item = ({ task, i }) => {
        return (
            <TouchableOpacity style={styles.peritem} key={i} onPress={getProfile(task)}>
                <Task task={task} />
            </TouchableOpacity>
        )
    }

    const closeProfile = () => {
        setShowProfile(!showProfile)
    }

    const getProfile= (task)=>{
        setShowProfile(true)
        setTask(task)
    }

    return (taskItems &&
        <View style={styles.container}>
            <View style={styles.taskWrapper}>
                <Text style={styles.sectionTitle}>
                    Se listan los perfiles
                </Text>
                <View style={styles.items}>
                    <FlatList
                        data={taskItems}
                        renderItem={(item, i) => <Item task={item} i={i} />}
                    >
                    </FlatList>
                </View>
            </View>
            <Modal animationType="slide"
                transparente={true}
                visible={showProfile}
                oneRequestClose={() => {
                    Alert.alert("modal has been closed.")
                    setShowProfile(!showProfile)
                }}
            >
                <View style={styles.centeredView}>
                    <View style={styles.modalView}>
                        <Tetx style={styles.modalText}>
                            <Profile task={task} closeProfile={closeProfile} />
                        </Tetx>
                    </View>

                </View>
            </Modal>

        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "gray",
        margeinTop: StatusBar.currentHeight || 0,
        dsiplay: "flex"
    },
    taskWrapper: {
        paddingTop: 80,
        paddingHorizontal: 2,
        height: 900
    },
    sectionTitle: {
        fontSize: 24,
        fontWeight: "bold"
    },
    items: {

    },
    peritem: {

    }

})
